import React from 'react';
import ReactDOM from 'react-dom';

const Header = ({ courses }) => {
  return (
    <div>
      {courses.map((course) => {
        return course.name
      })}
    </div>
  )
}

const Total = ({ courses }) => {
  return(
    <div>
      {courses.map((course) => {
        <strong>total of {course.parts.reduce((sum, course ) => sum += course.part.exercises, 0)} exercises</strong>
      })}

    </div>
  ) 
}

const Part = (props) => {
  // console.log(props)
  return (
    <p>
      {props.part.name} {props.part.exercises}
    </p>    
  )
}

const Content = ({ courses }) => {
  console.log(courses)
  return (
    <div>
      {courses.map(course =>
        course.parts.map((part) => {
          <Part key={part.id} part={part} />
        })
      )}
    </div>
  )
}

const Course = ({ courses }) => {
  return (
    <div>
      <Header courses={courses}/>
      <Content courses={courses}/>
      <Total courses={courses}/>
    </div>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return <Course courses={courses} />
}

ReactDOM.render(<App />, document.getElementById('root'))
