import React, { useState, useEffect } from 'react'
import axios from 'axios'

const App = () => {
  const [ country, setCountry ] = useState('');
  const [ countriesInfo, setCountriesInfo ] = useState([]);
  const [ dataResponse, setDataResponse ] = useState([]);
  const [ dataDisplayed, setDataDisplayed ] = useState('')
  const [ weatherDisplayed, setWeatherDisplayed ] = useState([])
  const [ weather, setWeather ] = useState({})
  const [ location, setLocation ] = useState('')

  const hook = () => {
    axios.get('https://restcountries.eu/rest/v2/all').then(response => {
      setCountriesInfo(response.data)
    })
    handleCountryChange()
  }

  const getWeather = () => {
    axios.get('http://api.weatherstack.com/current', {
      params: {
        access_key: process.env.REACT_APP_API_KEY,
        query: location
      }
    }).then(response => {
      if (location) {
        const cityWeather = {
          temperature: response.data.current.temperature,
          icon: response.data.current.weather_icons[0],
          speed: response.data.current.wind_speed,
          direction: response.data.current.wind_dir
        }
        setWeather(cityWeather)
      } else {
        return;
      }

    })
  }

  console.log(weather.temperature)


  const handleCountryChange = () => {

    const found = countriesInfo.findIndex((countryInfo) => {
      return countryInfo.name.includes(country)
    })

    if( found === -1 ) {
      return;
    }
    else if ( found > -1 ) {
      const updatedCountry = countriesInfo.filter((countryInfo) => {
        return countryInfo.name.includes(country)
      })
      setDataResponse(updatedCountry)

    }
 

    if ( dataResponse.length >  10) {

      setDataDisplayed('Too many matches, specify another filter')
    }
    else if ( dataResponse.length < 10 && dataResponse.length > 1 ) {
      const returnedData = dataResponse.map((data) => {
         
        return <div>
                <span key={data.numericCode}>{data.name}</span>
                <button name={data.name}onClick={(e) => {setCountry(e.target.name)}}>show</button>
               </div>
        
      })
      setDataDisplayed(returnedData)
      // console.log(dataResponse)
    }
    else if ( dataResponse.length === 1 && weather.length > 0 ) {
      setLocation(dataResponse[0].name)
      console.log(location)
      getWeather()
      console.log(weather)
      const returnedData = dataResponse.map((data) => {
 
        return <div key={data.numericCode}>
                <h1>{data.name}</h1>
                <p>capital {data.capital}</p>
                <p>population {data.population}</p>
                <h4>languages</h4>
                <ul>
                  {data.languages.map((language) => {
                    return <li key={data.languages.nativeName}>{language.name}</li>
                  })}
                </ul>
                <img src={data.flag} width="100" height="100"/>
                
               </div>
        
      })
      setDataDisplayed(returnedData)

      const returnedWeather = weather.map((el) => {
        
        return <div>

                <strong>temperature: {el.temperature}</strong>
                <img src={el.icon}/>
                <p><strong>wind:</strong> {el.direction} mph direction {el.direction}</p>
               </div>
      })

      setWeatherDisplayed(returnedWeather)

    }


  }

  useEffect(hook, [ country, dataResponse.length ])
  useEffect(getWeather, [dataResponse.length, location.length])
  // useEffect(weather, [dataResponse.length, location.length])



  return (
    <div>
      <form>
        find countries
        <input
        onChange={(e) => {setCountry(e.target.value)}}
        value={country}>
        </input>
      </form>
      {dataDisplayed}
      {weatherDisplayed}
    </div>
  )
}

export default App
