import React, { useState } from 'react'


const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas'}
  ])
  const [ newName, setNewName ] = useState('')

  const updatePersons = (e) => {

    e.preventDefault();

    const found = persons.find(person => person.name === newName)

    if( !found ){

      const updatedPersons = persons.concat({name: newName}) 
      setPersons(updatedPersons)
      setNewName('') 
    } else {
      alert(`${newName} is already added to phonebook`)
      setNewName('') 
    }; 
  };

  const handleNameChange = (e) => {
    setNewName(e.target.value)
    
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={updatePersons}>
        <div>
          name: <input
            onChange={handleNameChange}
            value={newName}
            type="text"
            name="option"/>
        </div>
      </form>
      <form>
        <div>
          number: <input
            onChange
            value={newName}
            type="text"
            name="option"/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
          {persons.map((person) => {
            person.id = persons.length * Math.random()
            return <p key={person.id}>{person.name}</p>
          })}
      </div>
    </div>
  )
}

export default App
