import React, { useState } from 'react'

const Filter = ({ newSearch, handleFilter }) => {
  return (
    <form>
    filter shown with
    <input
    value={newSearch}
    onChange={handleFilter}/>
  </form>
  )
};

const PersonForm = ({ updateInfo, handleNameChange, newName, handleNumberChange, newNumber }) => {
  return (
    <div>
      <form onSubmit={updateInfo}>
      name: <input
        onChange={handleNameChange}
        value={newName}/>
      </form>
      <form onSubmit={updateInfo}>
      number: <input
        onChange={handleNumberChange}
        value={newNumber}
        type="text"
        name="option"/>

      <button type="submit">add</button>
      </form>
    </div>
  )
};

const Persons = ({ infoToShow, persons }) => {
  return (
    <div>
    {infoToShow.map((info) => {
      info.id = persons.length * Math.random()
      return <p key={info.id}>{info.name} {info.number}</p>})}
</div>
  )
}


const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }]);
  const [ newName, setNewName ] = useState('');
  const [ newNumber, setNewNumber ] = useState('');
  const [ newSearch, setNewSearch] = useState('')
  const [ showAll, setShowAll ] = useState(undefined);

  const handleNameChange = (e) => {
    setNewName(e.target.value);
    
  };

  const updateInfo = (e) => {

    e.preventDefault();

    const found =
      persons
        .findIndex(person => person
          .name === newName
          .split(' ')
          .map(s => s.charAt(0)
          .toUpperCase() + s
          .substr(1))
          .join(' '));

    if( found === -1 ){

      const updatedInfo = persons
      .concat({
        name: newName
        .split(' ')
        .map(s => s
          .charAt(0)
          .toUpperCase() + s
          .substr(1))
          .join(' '),
          number: newNumber
        }) 
      setPersons(updatedInfo)
      setNewName('')
      setNewNumber('')
      console.log(persons)
      console.log(newName)
      console.log(newNumber)
    } else {

      alert(`${newName} is already added to phonebook`)
      setNewName('')
      setNewNumber('')
    }; 
  };

  const handleNumberChange = (e) => {
    setNewNumber(e.target.value)
  };


  const handleFilter = (e) => {
    setNewSearch(e.target.value)

    const found = 
    persons
      .findIndex(person => person
        .name
        .includes(newSearch
          .split(' ')
          .map(s => s
            .charAt(0)
            .toUpperCase() + s
            .substr(1))
            .join(' ')));

    if(found === -1) {
      setShowAll(false)
    } else {
      setShowAll(true)
    }

  };

  const infoToShow = showAll ? persons.filter(person => person.name.includes(newSearch.split(' ').map(s => s.charAt(0).toUpperCase() + s.substr(1)).join(' ')) || person.name.includes(newSearch)) : persons;



  return (
    <div>
      <h2>Phonebook</h2>

      <Filter newSearch={newSearch} handleFilter={handleFilter}/>

      <h3>add a new</h3>

      <PersonForm updateInfo={updateInfo} handleNameChange={handleNameChange} handleNumberChange={handleNumberChange} newName={newName} newNumber={newNumber}/>

      <h3>Numbers</h3>

      <Persons infoToShow={infoToShow} persons={persons}/>

    </div>
  )
}

export default App
