import React, { useState } from 'react'


const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas'},
  ])
  const [ newName, setNewName ] = useState('')

  const updatePersons = (e) => {
    e.preventDefault();


persons.map(person => {
//     const personObject = {
//           name: newName,
//         }
// person.name !== newName ? setPersons(persons.concat(personObject)) :  setNewName('')

      if(person.name !== newName) {
      
        const personObject = {
          name: newName,
        }
        setPersons(persons.concat(personObject))
        console.log(newName, "if")
        console.log(person.name, "if")
       
      } else {
        setNewName('')
        console.log(newName, "else")
        console.log(person.name, "else")
      }

    })
     

        
}

  const handleNoteChange = (e) => {
    setNewName(e.target.value)
    
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={updatePersons}>
        <div>
          name: <input
            onChange={handleNoteChange}
            value={newName}
            type="text"
            name="option"/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
          {persons.map((person) => {
            person.id = persons.length * Math.random()
            return <p key={person.id}>{person.name}</p>
          })}
      </div>
    </div>
  )
}

export default App
