import React, { useState, useEffect } from 'react'
import axios from 'axios'

const App = () => {
  const [ country, setCountry ] = useState('')

  const handleCountryChange = (e) => {
    setCountry(e.target.value)
    console.log(country);
  }


  return (
    <div>
      <form>
        find countries
        <input
        onChange={handleCountryChange}
        value={country}>

        </input>
      </form>
    </div>
  )
}

export default App
